sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }
    // Remove commas from the value
    var cleanValue = newValue.replace(/,/g, '');
    // Set the cleaned value back to the field
    g_form.setValue('StartUp_Year', cleanValue);
    //function onChange(control, oldValue, newValue, isLoading) {
    if (cleanValue < 1900 || cleanValue > 2100) {
        g_form.showFieldMsg(cleanValue, 'Please enter a valid year between 1900 and 2100', 'error');
        g_form.clearValue(cleanValue);
    }
}
